import Users from "./components//User/Users";

import "./App.css";
import Posts from "./components/Post/Posts";
import React, { useEffect, useState } from "react";
import axios from "axios";

function App() {

  const [userId,setUserId] = useState();
  const [posts,setPosts] = useState([]);
  const POSTS_URL = "https://jsonplaceholder.typicode.com/posts?userId=";

  useEffect(()=>{
    axios.get(POSTS_URL+userId).then((res=> setPosts(res.data)));
  },[userId])

  const handleSetUser = (userId)=>{
    setUserId(userId);
  }

  return (  
    <div className="flex-container row">
      <div className="flex-grow 4">
        <Users handleSetUser={handleSetUser}/>
      </div>
      <div className="flex-grow 8">
        <Posts posts={posts}/>
      </div>
    </div>
  );
}

export default App;
