import Users from "./components//User/Users";

import "./App.css";
import Posts from "./components/Post/Posts";
import React, { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const [userId, setUserId] = useState();
  const [posts, setPosts] = useState([]);
  const POSTS_URL = "https://jsonplaceholder.typicode.com/posts?userId=";

  useEffect(() => {
    axios.get(POSTS_URL + userId).then((res) => setPosts(res.data));
  }, [userId]);

  const handleSetUser = (userId) => {
    setUserId(userId);
  };

  return (
    <div className="container">
      <div className="title-container">
        <div className="users-title">
          <h5>Users</h5>
        </div>
        <div className="posts-title">
          <h5>Posts</h5>
        </div>
      </div>

      <div className="users">
        <Users handleSetUser={handleSetUser} />
      </div>

      <div className="posts">
        <Posts posts={posts} userId={userId} />
      </div>
    </div>
  );
}

export default App;
