import Users from "./components//User/Users";

import "./App.css";
import Posts from "./components/Post/Posts";
import React, { useEffect, useState } from "react";
import axios from "axios";
import Header from "./components/Header";
import { Route, Router, Routes } from "react-router-dom";


function App() {
  const [userId, setUserId] = useState();
  const [posts, setPosts] = useState([]);
  const POSTS_URL = "https://jsonplaceholder.typicode.com/posts?userId=";

  useEffect(() => {
    axios.get(POSTS_URL + userId).then((res) => setPosts(res.data));


  }, [userId]);

  const handleSetUser = (userId) => {
    setUserId(userId);
  };

  return (
    <Router>
      <div className="main-container">
      <div>
        <Header />
      </div>
      <div className="horizontal-line"></div>
      <Routes>
        <Route path="/">
        <div className="container">
        <div className="users">
          <Users handleSetUser={handleSetUser}></Users>
        </div>
        <div className="posts" >
          <Posts posts={posts} userId={userId}></Posts>
        </div>
      </div>
        </Route>
      </Routes>
    </div>
    </Router>
  );
}

export default App;
