import { findNonSerializableValue } from "@reduxjs/toolkit";
import React from "react";
import { NavLink ,useLocation} from "react-router-dom";
import "./Header.css";
const Header = () => {

  const isActive = {
    textDecoration : "none",
    textColor:"black",
    fontSize:"40px"
  }

  const inActive = {
    textDecoration:"none",
    textColor:"black",
    fontSize:"30px",
  }

  return (
    <div className="title-container">
      <div className="users-title">
        <h5>Users</h5>
      </div>
      <div className="header-vertical-line"></div>
      <div className="posts-title">
        <NavLink to="/posts" style={isActive}>
          <h5>Posts</h5>
        </NavLink>
      </div>
      <div className="todos-title">
        <NavLink to="/todos" style={location==='/todos'? isActive : inActive }>
          <h5>Todos</h5>
        </NavLink>
      </div>
    </div>
  );
};

export default Header;
