import { createReducer } from "@reduxjs/toolkit";

const initialState ={
    selectedUser:''
}


export default createReducer(initialState,(builder)=>{
    builder.addCase('UPDATE_TODO_STATUS_FILTER',(state,action)=>{
        state.selectedUser = action.payload
    })
})
