import React from "react";
import { useState, useEffect } from "react";

export default function User({ name, username }) {
  const IMAGE_URL = "https://api.lorem.space/image/face?w=150&h=150";

  const [usersImage, setUserImage] = useState([]);

  useEffect(() => {
    axios
      .get(IMAGE_URL)
      .then((response) => setUserImage(response.data))

  }, []);
  return (
    <div>
      <img src="" alt="Avatar" />
      <div>{name}</div>
      <p>{username}</p>
    </div>
  );
}
