import React from "react";
import { useState, useEffect } from "react";
import axios from "axios";
import './User.css'
export default function User({ name, username }) {
  const IMAGE_URL = "https://api.lorem.space/image/face?w=50&h=50";

  const [usersImage, setUserImage] = useState([]);

  useEffect(() => {
    axios
      .get(IMAGE_URL)
      .then((response) => setUserImage(response))

  }, []);

  return (
    <div className=".user">
      <img className=".profile" src={IMAGE_URL} />
      <p className=".name">{name}</p>
      <p className=".username">{username}</p>
    </div>
  );
}
