import { configureStore } from "@reduxjs/toolkit";


const selectedUserReducer = (state = {selectedUserId:0}, action) => {
    if(action.type === 'changeUser'){
        return {
            selectedUserId : 1
        }
    }
}; 


const store = configureStore( {
    reducer:{
        user:selectedUserReducer
    }
});

export default store;
