import { configureStore } from "@reduxjs/toolkit";


const selectedUserReducer = (state = {selectedUserId}, action) => {
    if(action.type === 'changeUser'){
        return {
            selectedUserId : 1
        }
    }
    else{
        return selectedUserId=null;
    }
}; 


const store = configureStore( selectedUserReducer);

export default store;
